<script>
import paginate from 'jw-paginate'

const defaultLabels = {
  first: 'First',
  last: 'Last',
  previous: 'Previous',
  next: 'Next'
}
const defaultStyles = {
  ul: {
    margin: 0,
    padding: 0,
    display: 'inline-block'
  },
  li: {
    listStyle: 'none',
    display: 'inline',
    textAlign: 'center'
  },
  a: {
    cursor: 'pointer',
    padding: '6px 12px',
    display: 'block',
    float: 'left'
  }
}
export default {
  props: {
    items: {
      type: Array,
      required: true
    },
    initialPage: {
      type: Number,
      default: 1
    },
    pageSize: {
      type: Number,
      default: 10
    },
    maxPages: {
      type: Number,
      default: 1
    },
    labels: {
      type: Object,
      default: () => defaultLabels
    },
    styles: {
      type: Object
    },
    disableDefaultStyles: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
      pager: {},
      ulStyles: {},
      liStyles: {},
      aStyles: {}
    }
  },
  created () {
    if (!this.$listeners.changePage) {
      throw 'Missing required event listener: "changePage"'
    }
    // set default styles unless disabled
    if (!this.disableDefaultStyles) {
      this.ulStyles = defaultStyles.ul
      this.liStyles = defaultStyles.li
      this.aStyles = defaultStyles.a
    }
    // merge custom styles with default styles
    if (this.styles) {
      this.ulStyles = { ...this.ulStyles, ...this.styles.ul }
      this.liStyles = { ...this.liStyles, ...this.styles.li }
      this.aStyles = { ...this.aStyles, ...this.styles.a }
    }
    // set to initial page
    this.setPage(this.initialPage)
  },
  methods: {
    setPage (page) {
      const {
        items,
        pageSize,
        maxPages
      } = this
      if (page < 1) {
        this.$router.push({
          name: 'siswa-tantangan'
        })
      }
      // get new pager object for specified page
      const pager = paginate(items.length, page, pageSize, maxPages)
      // get new page of items from items array
      const pageOfItems = items.slice(pager.startIndex, pager.endIndex + 1)
      // update pager
      this.pager = pager
      // emit change page event to parent component
      this.$emit('changePage', pageOfItems)
    }
  },
  watch: {
    items () {
      this.setPage(this.initialPage)
    }
  }
}
</script>
